jQuery(document).ready(function($){"use strict";$("code.uploaded-file-url").each(function(){if($(this).html().trim()!==""){$(this).css("display","inline-block")}});$("img.uploaded-image").each(function(){if($(this).attr("src").trim()!==""){$(this).css("display","block")}});$(".upload, .image img, .url code").click(function(e){e.preventDefault();const dataID=$(this).data("field-id");wp.media.editor.send.attachment=function(props,attachment){const current="";if(attachment.sizes&&attachment.sizes.thumbnail&&attachment.sizes.thumbnail.url){$(current+" .image img").attr("src",attachment.sizes.thumbnail.url);$(current+" .image img").css("display","block")}$(current+" .url code").html(attachment.url).show();$(current+" .attachment_id").val(attachment.id);$(current+" .remove").show();$(current+" .upload").hide()};wp.media.editor.open();return false});$(".remove").click(function(e){e.preventDefault();const dataID=$(this).parent().attr("data-id");const current='[data-id="'+dataID+'"]';$(current+" .url code").html("").hide();$(current+" .attachment_id").val("");$(current+" .image img").attr("src","");$(current+" .image img").css("display","none");$(current+" .remove").hide();$(current+" .upload").show()});const elementColorPicker=$(".colorpicker");if(elementColorPicker.length){elementColorPicker.wpColorPicker()}const elementNavTabs=$(".nav-tab");if(elementNavTabs.length){elementNavTabs.click(function(e){e.preventDefault();const id=$(this).attr("href").substring(1);$(".tab-content").hide();$("#"+id).show();$(".nav-tab").removeClass("nav-tab-active");$(this).addClass("nav-tab-active")})}});